sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var animalCategory = g_form.getValue('category');

	var animalNumber = g_form.getValue('number');
	var categoryArray = animalNumber.split('');
	var lastFiveChars = categoryArray.slice(-5);
	var resultString = lastFiveChars.join('');

    switch (animalCategory) {
        case 'Cow':
			g_form.setValue('id', 'COW' + resultString);
			break;
		case 'Pig':
			g_form.setValue('id','PIG' + resultString);
			break;
		case 'Sheep':
			g_form.setValue('id','SHEEP' + resultString);
			break;
		case 'Goat':
			g_form.setValue('id','GOAT' + resultString);
			break;
		case 'Chicken':
			g_form.setValue('id','CHICKEN' + resultString);
			break;
		case 'Duck':
			g_form.setValue('id','DUCK' + resultString);
			break;
    }

    //Type appropriate comment here, and begin script below

}
